import './App.css';
import AboutUs from './components/AboutUs';
import ClientSection from './components/ClientSection';
import Footer from './components/Footer';
import InfoSection from './components/InfoSection';
import Navbar from './components/Navbar';
import Projects from './components/Projects';
import Contact from './components/Contact'
import Services from './components/Services';
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

// includes pages
import HomePage from './pages/HomePage'
import AboutPage from './pages/AboutPage'
import CareersPage from './pages/CareersPage'
import ProjectsPage from './pages/ProjectsPage'
import ServicesPage from './pages/ServicesPage'
import Nopage from './pages/Nopage'


const menu = document.getElementById('menu')
const openMenuBtn = document.getElementById('menu-icon')
const closeMenuBtn = document.getElementById('close')
const primaryActionBtns = document.querySelector('.primary-action-btns')

// openMenuBtn.addEventListener('click', openMenu)
// closeMenuBtn.addEventListener('click', closeMenu)
// window.addEventListener('click', outsideClick)

function openMenu() {
  menu.style.display = 'block'
  primaryActionBtns.style.display = 'none'
}

function closeMenu() {
  menu.style.display = 'none'
}

function outsideClick(e) {
  if (e.target == menu) {
    closeMenu()
  }
}



function App() {

  // collapsible services
  var coll = document.getElementsByClassName("collapsible");
  var i;

  for (i = 0; i < coll.length; i++) {
    coll[i].addEventListener("click", function () {
      this.classList.toggle("active");
      var content = this.nextElementSibling;
      if (content.style.maxHeight) {
        content.style.maxHeight = null;
      } else {
        content.style.maxHeight = content.scrollHeight + "px";
      }
    });
  }


  return (
    <Router>
      <Navbar />
      <div className="main-page-content">
        <Routes>
          <Route path='/' element={<HomePage />} />
          <Route path='/about' element={<AboutPage />} />
          <Route path='/services' element={<ServicesPage />}>
            <Route path='/cadastralandengineering' element={<En />} />
          </Route>
          <Route path='/projects' element={<ProjectsPage />} />
          <Route path='/careers' element={<CareersPage />} />
          <Route path='*' element={<Nopage />} />
        </Routes>
      </div>
      <Footer />
    </Router>
  );
}

export default App;
