import { useState, useRef, useEffect } from 'react'
import { Link } from 'react-router-dom'
import autoAnimate from '@formkit/auto-animate'

const CollapsibleServices = () => {
  const [show, setShow] = useState(false)
  const parent = useRef(null)

  useEffect(() => {
    parent.current && autoAnimate(parent.current)
  }, [parent])

  const reveal = () => setShow(!show)

  return <div ref={parent}>
    <strong className="collapsibleServices-label" onClick={reveal}>Services</strong>
    { show && <p className="collapsibleServices-content" >
    <div>
        <ul>
          <li><Link className='bold' to="/services">All Services</Link></li>
          <li><Link className='bold' to="/about">About Us</Link></li>
          <li><Link className='bold' to="/services">Services</Link></li>
          <li><Link className='bold' to="/projects">Projects</Link></li>
          <li><Link className='bold' to="careers">Careers</Link></li>
        </ul>
      </div>
    </p> }
  </div>
}

export default CollapsibleServices
